if Rails.env.production?
  environment = ENV['REGION'] # Loaded fixtures on production are determined by a environment variable
  resources_path = ENV['RESOURCES_PATH']
end

if admin_user
  #Set up ActiveAdmin user
  AdminUser.delete_all
  AdminUser.new(:email => "SET_ADMIN@USER.EMAL", :password => admin_pass, :password_confirmation => admin_pass).save
end

if load_fixtures
  # Add the filenames (without .yml) to be imported
  %w[categories nominees contacts].each do |model|
    ActiveRecord::Fixtures.create_fixtures("#{Rails.root}/db/#{environment}",model)
  end
else
  if demo_categories
    # Categories
    Category.delete_all

    %w[Action Animation Comedy Documentary Family Film-Noir Horror Musical].each do |cat|
      Category.create(:name => cat, :is_individual => false).save
    end
    %w[Popular  Adventure  Biography  Crime  Drama  Fantasy  History  Music ].each do |cat|
      Category.create(:name => cat, :is_individual => true).save
    end
  end

  if demo_nominees
    # Nominees
    Nominee.delete_all

    names.each do |name|
      offset = Category.first.id
      category = Category.find(offset + rand(Category.count))
      namedown = name.downcase
      web = 'www.'+namedown +'.com'
      random = rand(10)+1
      logo = 'http://lorempixel.com/g/400/400/food/' + random.to_s
      description = "Lorem ipsum dolor sit amet, consectetur adipisicing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat."
      Nominee.create(:name => name, :website => web, :country => country.sample, :category => category, :description => description, :logo => logo)
    end
  end

  if demo_users
    # Delete spoof users
    User.where('provider != ?','facebook').delete_all

    for i in 0..300
      name = user_first_names.sample + ' ' + user_last_names.sample
      User.create(:name => name, :provider => 'generated').save
    end
  end

  if demo_votes
    # Votes - pre-requisites are nominees and users
    User.all.each do |u|
      offset = Nominee.first.id
      rand_nominee = Nominee.find(offset + rand(Nominee.count))
      Vote.create(:user => u, :nominee => rand_nominee).save
    end
  end

  if demo_contacts
    # Contacts
    for i in 0..35
      first = user_first_names.sample
      last = user_last_names.sample
      name =  first + ' ' + last
      linkedin = 'http://linkedin.com/' + first + '-' + last
      random = rand(10)+1
      picture = 'http://lorempixel.com/g/200/200/people/' + random.to_s

      offset = Nominee.first.id
      rand_nominee = Nominee.find(offset + rand(Nominee.count))

      Contact.create(:name => name, :position => job_titles.sample, :email => user_emails.sample, :linkedin => linkedin, :nominee => rand_nominee, :picture => picture)
    end
  end
